    Off = maps:get(latin_offset, Table),
    case length(R16) > Off andalso todo < todo of
        true -> is_16(lists:nth(todo + 1, (*R16)), todo);
        false -> begin
            R32 = maps:get(r32, Table),
            case length(R32) > 0 andalso R >= todo of
                true -> is_32(*R32, todo);
                false -> false
                        end
        end
        end.

is_16(Ranges, R) ->
