    Ch_len = 0,
    case length(S) > 0 of
        true -> begin
            Ch_len1 = utf8_char_len(lists:nth(Index + 1, S)),
            case Ch_len1 == 1 of
                true -> todo;
                false -> ok
            end,
            case Ch_len1 > 1 andalso Ch_len1 < 5 of
                true -> begin
                    Lword = 0,
                    % TODO: unhandled stmt type
                    ok                    case Ch_len1 == 2 of
                        true -> ok;
                        false -> case Ch_len1 == 3 of
                            true -> ok;
                            false -> case Ch_len1 == 4 of
                                true -> ok;
                                false -> ok
                            end
                        end
                    end
                end;
                false -> ok
            end
        end;
        false -> ok
    end,
    Res.

raw_index(S, Index) ->
    R = [],
    % TODO: unhandled stmt type
    ok    'rune.str'(lists:nth(Index + 1, R)).

reverse(S) ->
    Len_s = len(S),
    case Len_s == 0 orelse Len_s == 1 of
        true -> 'string.clone'(S);
        false -> begin
            Str_array = [],
            lists:foreach(fun(I) ->
                Str_array bsl raw_index(S, I),
                ok
            end, lists:seq(0, Len_s - 1)),
            Str_array1 = '[]string.reverse'(Str_array),
            '[]string.join'(Str_array1, <<"">>)
        end
        end.

to_upper(S) ->
    convert_case(S, true).

to_lower(S) ->
    convert_case(S, false).

is_punct(S, Index) ->
    is_rune_punct(get_rune(S, Index)).

