/* Align a ptr_t pointer down/up to a given boundary.               */
#define PTR_ALIGN_DOWN(p, b) ((ptr_t)((word)(p) & ~(word)((b)-1)))
#define PTR_ALIGN_UP(p, b) \
                ((ptr_t)(((word)(p) + (word)((b)-1)) & ~(word)((b)-1)))

